import { useEffect,useState } from 'react';
import { ethers } from 'ethers';
import wallet from "./artifacts/contracts/Wallet.sol/Wallet.json";
import './App.css';

const walletAdersse="0x5FbDB2315678afecb367f032d93F642f64180aa3";


function App() {
  const [balance, setBalance] = useState();
  const [amount, setAmount] = useState();
  const [amountWithdraw, setAmountWithdraw] = useState();
  const [error, setError] = useState();
  const [success, setSuccess] = useState();

  useEffect(()=>{
    getBalance();
  },[])

  async function getBalance(){
      if(typeof window.ethereum !== "undefined"){
        const accounts=await window.ethereum.request({method:"eth_requestAccounts"});
        const provider=new ethers.providers.Web3Provider(window.ethereum);
        const contract=new ethers.Contract(walletAdersse,wallet.abi,provider);

       try {
          let overide={
            from:accounts[0]
          }
          const data= await contract.getBalance(overide);
          setBalance(String(data));
       } catch (error) {
          setError(error)
       }

      }
  }
  async function snedMoney(){
    if(!amount)return;



    if(typeof window.ethereum !== "undefined"){
      const accounts=await window.ethereum.request({method:"eth_requestAccounts"});
      const provider=new ethers.providers.Web3Provider(window.ethereum);
      const signer=provider.getSigner();


      try {
        const override={

        value:ethers.utils.parseEther(amount),
        to:walletAdersse,
        from:accounts[0]
        }
        const transaction=await signer.sendTransaction(override);
        await transaction.wait();

       setSuccess("You have send money to your waller");

      } catch (error) {
          console.log('====================================');
          console.log(error);
          console.log('====================================');
      }

    }
  }
  return (
    <div className="App">
      {success && <p>{success}</p>}
      <input type="text" placeholder="Put the amount tha you want to send" onChange={(e)=>{setAmount(e.target.value)}}/>
      <h1>{balance / 10**18}</h1>
      <button onClick={snedMoney}>Withdraw money to your wallet</button>

    </div>
  );
}

export default App;
